      dailyYieldMere: "3.20",
      roiDays: 175,
      rarity: "epic",
      isAvailable: true,
    },
    {
      name: "Pyramid Elite",
      description: "Elite-tier supercomputer with holographic circuitry",
      imageUrl: "/attached_assets/generated_images/Black_Background_Pyramid_Elite_826f6c77.png",
      thRate: 25.0,
      basePriceUsd: "349.75",
      basePriceMere: "699.50",
      dailyYieldUsd: "2.00",
