      icon: "Grid3x3",
      category: "mining",
      criteria: { type: "slots_filled", value: 6 },
      rewardMere: "25.00",
      tier: "silver",
    },
    {
      name: "Hash Power",
      description: "Reach 10 TH/s total hashrate",
      icon: "TrendingUp",
      category: "mining",
      criteria: { type: "total_hashrate", value: 10 },
      rewardMere: "30.00",
      tier: "silver",
    },
    {
      name: "Mining Tycoon",
      description: "Reach 50 TH/s total hashrate",
      icon: "Crown",
      category: "mining",
      criteria: { type: "total_hashrate", value: 50 },
      rewardMere: "100.00",
      tier: "gold",
    },
