client/src/pages/Leaderboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { BottomNav } from "@/components/BottomNav";
import { Trophy, Medal, Crown, Zap, TrendingUp } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { formatMERE } from "@/lib/constants";
import type { LeaderboardEntry, User } from "@shared/schema";

interface LeaderboardEntryWithUser extends LeaderboardEntry {
  user: User;
}

export default function Leaderboard() {
  const { user: currentUser } = useAuth();

  // Fetch leaderboard
  const { data: leaderboard = [], isLoading } = useQuery<LeaderboardEntryWithUser[]>({
    queryKey: ["/api/leaderboard"],
  });

  // Fetch current season
  const { data: season } = useQuery<{ id: string; name: string; endAt: string }>({
    queryKey: ["/api/leaderboard/season"],
  });

  const getRankIcon = (rank: number) => {
    switch (rank) {
      case 1:
        return <Crown className="w-6 h-6 text-gold-DEFAULT" />;
      case 2:
        return <Medal className="w-6 h-6 text-gray-400" />;
      case 3:
        return <Medal className="w-6 h-6 text-amber-700" />;
      default:
        return null;
    }
  };

  const getRankBadge = (rank: number) => {
    switch (rank) {
      case 1:
        return <Badge className="bg-gold-gradient text-black font-bold">1st</Badge>;
      case 2:
        return <Badge className="bg-gradient-to-r from-gray-300 to-gray-500 text-white font-bold">2nd</Badge>;
      case 3:
        return <Badge className="bg-gradient-to-r from-amber-600 to-amber-800 text-white font-bold">3rd</Badge>;
      default:
        return <Badge variant="secondary">{rank}th</Badge>;
    }
  };

  const daysLeft = season?.endAt ? Math.ceil((new Date(season.endAt).getTime() - Date.now()) / (1000 * 60 * 60 * 24)) : 0;

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center pb-20">
        <div className="animate-pulse text-primary">Loading leaderboard...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-background via-background to-accent/5 pb-20">
      {/* Header */}
      <div className="sticky top-0 z-40 bg-card/95 backdrop-blur-sm border-b border-border">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <h1 className="font-display font-bold text-2xl bg-gold-gradient bg-clip-text text-transparent flex items-center gap-2">
              <Trophy className="w-6 h-6 text-primary" />
              Ranks
            </h1>
            {season && (
              <div className="text-right">
                <div className="text-sm font-semibold text-foreground">{season.name}</div>
                <div className="text-xs text-muted-foreground">{daysLeft} days left</div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-6 space-y-6">
        {/* Top 3 Podium */}
        {leaderboard.length >= 3 && (
          <div className="grid grid-cols-3 gap-4 items-end mb-6">
            {/* 2nd Place */}
            <div className="text-center">
              <Card className="p-4 bg-gradient-to-br from-card to-gray-500/10 border-gray-400/30">
                <div className="flex justify-center mb-2">
                  <Medal className="w-8 h-8 text-gray-400" />
                </div>
                <Avatar className="w-16 h-16 mx-auto mb-2 border-2 border-gray-400">
                  <AvatarImage src={leaderboard[1]?.user.profileImageUrl || ""} />
                  <AvatarFallback className="bg-gray-500 text-white">
                    {leaderboard[1]?.user.firstName?.[0] || "U"}
                  </AvatarFallback>
                </Avatar>
                <div className="font-semibold text-sm truncate">
                  {leaderboard[1]?.user.firstName || "User"}
                </div>
                <div className="text-xs text-muted-foreground mb-2">
                  {formatMERE(leaderboard[1]?.totalMined || "0")} MERE
                </div>
                <Badge className="bg-gradient-to-r from-gray-300 to-gray-500 text-white text-xs">2nd</Badge>
              </Card>
            </div>

            {/* 1st Place */}
            <div className="text-center">
              <Card className="p-4 bg-gradient-to-br from-card to-primary/20 border-primary/50 relative">
                <div className="absolute -top-3 left-1/2 -translate-x-1/2">
                  <div className="bg-gold-gradient w-12 h-12 rounded-full flex items-center justify-center animate-pulse-glow">
                    <Crown className="w-6 h-6 text-black" />
                  </div>
                </div>
                <div className="mt-2" />
                <Avatar className="w-20 h-20 mx-auto mb-2 border-4 border-primary">
                  <AvatarImage src={leaderboard[0]?.user.profileImageUrl || ""} />
                  <AvatarFallback className="bg-primary text-primary-foreground text-xl">
                    {leaderboard[0]?.user.firstName?.[0] || "U"}
                  </AvatarFallback>
                </Avatar>
                <div className="font-bold text-base truncate">
                  {leaderboard[0]?.user.firstName || "User"}
                </div>
                <div className="text-xs text-muted-foreground mb-2">
                  {formatMERE(leaderboard[0]?.totalMined || "0")} MERE
                </div>
                <Badge className="bg-gold-gradient text-black font-bold">1st</Badge>
              </Card>
            </div>

            {/* 3rd Place */}
            <div className="text-center">
              <Card className="p-4 bg-gradient-to-br from-card to-amber-700/10 border-amber-700/30">
                <div className="flex justify-center mb-2">
                  <Medal className="w-8 h-8 text-amber-700" />
                </div>
                <Avatar className="w-16 h-16 mx-auto mb-2 border-2 border-amber-700">
                  <AvatarImage src={leaderboard[2]?.user.profileImageUrl || ""} />
                  <AvatarFallback className="bg-amber-700 text-white">
                    {leaderboard[2]?.user.firstName?.[0] || "U"}
                  </AvatarFallback>
                </Avatar>
                <div className="font-semibold text-sm truncate">
                  {leaderboard[2]?.user.firstName || "User"}
                </div>
                <div className="text-xs text-muted-foreground mb-2">
                  {formatMERE(leaderboard[2]?.totalMined || "0")} MERE
                </div>
                <Badge className="bg-gradient-to-r from-amber-600 to-amber-800 text-white text-xs">3rd</Badge>
              </Card>
            </div>
          </div>
        )}

        {/* Full Rankings */}
        <div>
          <h2 className="font-display font-bold text-lg mb-4">Rankings</h2>

          {leaderboard.length === 0 ? (
            <Card className="p-12 text-center border-card-border">
              <Trophy className="w-16 h-16 mx-auto mb-4 text-muted-foreground opacity-50" />
              <p className="text-muted-foreground">No rankings yet. Start mining to appear on the leaderboard!</p>
            </Card>
          ) : (
            <div className="space-y-2">
              {leaderboard.map((entry, index) => {
                const isCurrentUser = entry.userId === currentUser?.id;
                return (
                  <Card
                    key={entry.id}
                    className={`p-4 border-card-border hover-elevate ${
                      isCurrentUser ? "border-primary/50 bg-primary/5" : ""
                    }`}
                    data-testid={`leaderboard-entry-${index + 1}`}
                  >
                    <div className="flex items-center gap-4">
                      <div className="flex items-center justify-center w-12">
                        {getRankIcon(entry.rank || index + 1) || (
                          <div className="text-2xl font-display font-bold text-muted-foreground">
                            {entry.rank || index + 1}
                          </div>
                        )}
                      </div>

                      <Avatar className="w-12 h-12">
                        <AvatarImage src={entry.user.profileImageUrl || ""} />
                        <AvatarFallback className="bg-accent text-foreground">
                          {entry.user.firstName?.[0] || entry.user.email?.[0]?.toUpperCase() || "U"}
                        </AvatarFallback>
                      </Avatar>

                      <div className="flex-1 min-w-0">
                        <div className="font-semibold text-foreground flex items-center gap-2">
                          {entry.user.firstName || entry.user.email?.split("@")[0] || "Anonymous"}
                          {isCurrentUser && (
                            <Badge variant="outline" className="text-xs">You</Badge>
                          )}
                        </div>
                        <div className="flex items-center gap-3 text-sm text-muted-foreground">
                          <div className="flex items-center gap-1">
                            <TrendingUp className="w-3 h-3" />
                            <span>{formatMERE(entry.totalMined)} MERE</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <Zap className="w-3 h-3" />
                            <span>{entry.totalHashrate.toFixed(2)} TH/s</span>
                          </div>
                        </div>
                      </div>

                      <div>
                        {getRankBadge(entry.rank || index + 1)}
                      </div>
                    </div>
                  </Card>
                );
              })}
            </div>
          )}
        </div>

        {/* Season Rewards Info */}
        <Card className="p-6 bg-gradient-to-br from-card to-primary/10 border-primary/30">
          <h3 className="font-display font-bold text-lg mb-3 flex items-center gap-2">
            <Trophy className="w-5 h-5 text-primary" />
            Season Rewards
          </h3>
          <div className="space-y-2 text-sm">
            <div className="flex items-center justify-between">
              <span className="text-muted-foreground">1st Place:</span>
              <span className="font-semibold text-primary">1000 MERE + Legendary Miner</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-muted-foreground">2nd Place:</span>
              <span className="font-semibold">500 MERE + Epic Miner</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-muted-foreground">3rd Place:</span>
              <span className="font-semibold">250 MERE + Rare Miner</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-muted-foreground">Top 10:</span>
              <span className="font-semibold">100 MERE</span>
            </div>
          </div>
        </Card>
      </div>

      <BottomNav />
    </div>
  );
}

client/src/pages/Wallet.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { BottomNav } from "@/components/BottomNav";
import { Wallet as WalletIcon, ArrowDownToLine, ArrowUpFromLine, Copy, Check, ExternalLink, ArrowLeftRight } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { formatMERE, mereToUSD, usdToMERE, MERE_TO_USD_RATE } from "@/lib/constants";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import type { Transaction } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

export default function Wallet() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [showDeposit, setShowDeposit] = useState(false);
  const [showWithdraw, setShowWithdraw] = useState(false);
  const [showConvert, setShowConvert] = useState(false);
  const [withdrawAmount, setWithdrawAmount] = useState("");
  const [convertAmount, setConvertAmount] = useState("");
  const [convertDirection, setConvertDirection] = useState<"mere-to-usdt" | "usdt-to-mere">("mere-to-usdt");
  const [depositAddress, setDepositAddress] = useState("");
  const [copied, setCopied] = useState(false);
  const [withdrawAddress, setWithdrawAddress] = useState("");

  // Fetch transactions
  const { data: transactions = [] } = useQuery<Transaction[]>({
    queryKey: ["/api/wallet/transactions"],
  });

  // Generate deposit address
  const generateDepositMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/wallet/deposit/generate", {});
      return response;
    },
    onSuccess: (data: any) => {
      setDepositAddress(data.address);
    },
  });

  // Withdraw mutation
  const withdrawMutation = useMutation({
    mutationFn: async (data: { amountUsdt: string, address: string }) => {
      await apiRequest("POST", "/api/wallet/withdraw", { 
        amountMere: usdToMERE(parseFloat(data.amountUsdt)).toString(),
        address: data.address
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/wallet/transactions"] });
      setShowWithdraw(false);
      setWithdrawAmount("");
      setWithdrawAddress("");
      toast({
        title: "Withdrawal Initiated",
        description: "Your withdrawal is being processed",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Withdrawal Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Convert mutation
  const convertMutation = useMutation({
    mutationFn: async (data: { fromCurrency: string, toCurrency: string, amount: string }) => {
      await apiRequest("POST", "/api/wallet/convert", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/wallet/transactions"] });
      setShowConvert(false);
      setConvertAmount("");
      toast({
        title: "Conversion Successful",
        description: "Your funds have been converted",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Conversion Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleCopyAddress = () => {
    navigator.clipboard.writeText(depositAddress);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
    toast({
      title: "Copied!",
      description: "Address copied to clipboard",
    });
  };

  const handleDeposit = () => {
    setShowDeposit(true);
    if (!depositAddress) {
      generateDepositMutation.mutate();
    }
  };

  const handleWithdraw = () => {
    if (!withdrawAmount || parseFloat(withdrawAmount) <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid amount",
        variant: "destructive",
      });
      return;
    }
    if (!withdrawAddress || withdrawAddress.length < 10) {
      toast({
        title: "Invalid Address",
        description: "Please enter a valid USDT(TRC-20) wallet address",
        variant: "destructive",
      });
      return;
    }
    withdrawMutation.mutate({ amountUsdt: withdrawAmount, address: withdrawAddress });
  };

  const handleConvert = () => {
    if (!convertAmount || parseFloat(convertAmount) <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid amount",
        variant: "destructive",
      });
      return;
    }
    convertMutation.mutate({
      fromCurrency: convertDirection === "mere-to-usdt" ? "MERE" : "USDT",
      toCurrency: convertDirection === "mere-to-usdt" ? "USDT" : "MERE",
      amount: convertAmount
    });
  };

  const getTransactionIcon = (type: string) => {
    switch (type) {
      case "deposit":
        return <ArrowDownToLine className="w-4 h-4 text-status-online" />;
      case "withdrawal":
        return <ArrowUpFromLine className="w-4 h-4 text-status-busy" />;
      case "purchase":
        return <WalletIcon className="w-4 h-4 text-primary" />;
      case "earnings":
        return <TrendingUp className="w-4 h-4 text-primary" />;
      case "conversion":
        return <ArrowLeftRight className="w-4 h-4 text-primary" />;
      default:
        return <WalletIcon className="w-4 h-4 text-muted-foreground" />;
    }
  };

  const getTransactionColor = (type: string) => {
    switch (type) {
      case "deposit":
      case "earnings":
      case "reward":
        return "text-status-online";
      case "withdrawal":
      case "purchase":
        return "text-status-busy";
      case "conversion":
        return "text-primary";
      default:
        return "text-foreground";
    }
  };

  const mereBalance = parseFloat(user?.mereBalance || "0");
  const usdtBalance = parseFloat(user?.usdtBalance || "0");
  const withdrawAmountUsdt = parseFloat(withdrawAmount) || 0;
  const withdrawFee = withdrawAmountUsdt * 0.02; // 2% fee
  const withdrawTotal = withdrawAmountUsdt - withdrawFee;

  return (
    <div className="min-h-screen bg-gradient-to-b from-background via-background to-accent/5 pb-20">
      {/* Header */}
      <div className="sticky top-0 z-40 bg-card/95 backdrop-blur-sm border-b border-border">
        <div className="container mx-auto px-4 py-4">
          <h1 className="font-display font-bold text-2xl bg-gold-gradient bg-clip-text text-transparent flex items-center gap-2">
            <WalletIcon className="w-6 h-6 text-primary" />
            Wallet
          </h1>
        </div>
      </div>

      <div className="container mx-auto px-4 py-6 space-y-6">
        {/* Balance Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* MERE Balance Card */}
          <Card className="p-6 border-primary/30 bg-gradient-to-br from-card to-accent/20">
            <div className="text-center space-y-2">
              <div className="text-sm text-muted-foreground">MERE Balance</div>
              <div className="text-4xl font-display font-bold bg-gold-gradient bg-clip-text text-transparent" data-testid="text-mere-balance">
                {formatMERE(mereBalance)}
              </div>
              <div className="text-xs text-muted-foreground">MERE</div>
            </div>
          </Card>

          {/* USDT Balance Card */}
          <Card className="p-6 border-primary/30 bg-gradient-to-br from-card to-accent/20">
            <div className="text-center space-y-2">
              <div className="text-sm text-muted-foreground">USDT Balance</div>
              <div className="text-4xl font-display font-bold text-primary" data-testid="text-usdt-balance">
                {usdtBalance.toFixed(2)}
              </div>
              <div className="text-xs text-muted-foreground">USDT</div>
            </div>
          </Card>
        </div>

        {/* Exchange Rate Info */}
        <Card className="p-4 bg-accent/10 border-primary/10">
          <div className="text-center text-sm text-muted-foreground">
            Exchange Rate: <span className="text-foreground font-semibold">1 MERE = {MERE_TO_USD_RATE.toFixed(2)} USDT</span>
          </div>
        </Card>

        {/* Action Buttons */}
        <div className="flex gap-2 justify-center flex-wrap">
          <Button
            onClick={handleDeposit}
            className="flex-1 min-w-[140px] bg-gold-gradient text-black font-bold"
            data-testid="button-deposit"
          >
            <ArrowDownToLine className="w-4 h-4 mr-2" />
            Deposit
          </Button>
          <Button
            onClick={() => setShowWithdraw(true)}
            variant="outline"
            className="flex-1 min-w-[140px]"
            data-testid="button-withdraw"
          >
            <ArrowUpFromLine className="w-4 h-4 mr-2" />
            Withdraw
          </Button>
          <Button
            onClick={() => setShowConvert(true)}
            variant="secondary"
            className="flex-1 min-w-[140px]"
            data-testid="button-convert"
          >
            <ArrowLeftRight className="w-4 h-4 mr-2" />
            Convert
          </Button>
        </div>

        {/* Transaction History */}
        <div>
          <h2 className="font-display font-bold text-lg mb-4">Transaction History</h2>

          {transactions.length === 0 ? (
            <Card className="p-12 text-center border-card-border">
              <p className="text-muted-foreground">No transactions yet</p>
            </Card>
          ) : (
            <div className="space-y-2">
              {transactions.map((tx) => (
                <Card key={tx.id} className="p-4 border-card-border hover-elevate">
                  <div className="flex items-center gap-4">
                    <div className="w-10 h-10 rounded-full bg-accent flex items-center justify-center">
                      {getTransactionIcon(tx.type)}
                    </div>

                    <div className="flex-1 min-w-0">
                      <div className="font-semibold text-foreground capitalize">
                        {tx.description || tx.type}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {new Date(tx.createdAt!).toLocaleString()}
                      </div>
                      {tx.txHash && (
                        <div className="text-xs text-muted-foreground font-mono flex items-center gap-1 mt-1">
                          {tx.txHash.slice(0, 8)}...{tx.txHash.slice(-6)}
                          <ExternalLink className="w-3 h-3" />
                        </div>
                      )}
                    </div>

                    <div className="text-right">
                      <div className={`font-bold ${getTransactionColor(tx.type)}`}>
                        {tx.type === "withdrawal" || tx.type === "purchase" ? "-" : "+"}
                        {formatMERE(tx.amountMere)} MERE
                      </div>
                      {tx.amountUsd && (
                        <div className="text-sm text-muted-foreground">
                          {parseFloat(tx.amountUsd).toFixed(2)} USDT
                        </div>
                      )}
                      <div className="text-xs">
                        <span className={`inline-block px-2 py-0.5 rounded-full text-xs ${
                          tx.status === "completed" ? "bg-status-online/20 text-status-online" :
                          tx.status === "pending" ? "bg-status-away/20 text-status-away" :
                          "bg-status-busy/20 text-status-busy"
                        }`}>
                          {tx.status}
                        </span>
                      </div>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Deposit Dialog */}
      <Dialog open={showDeposit} onOpenChange={setShowDeposit}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Deposit USDT (TRC-20)</DialogTitle>
            <DialogDescription>
              Send USDT to this address to add funds to your wallet
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4 py-4">
            <Card className="p-4 bg-accent/20">
              <div className="text-sm text-muted-foreground mb-2">
                <strong>Important:</strong> Only send USDT (TRC-20) to this address. Other tokens will be lost.
              </div>
              <div className="text-sm text-muted-foreground">
                <strong>Exchange Rate:</strong> 1 USDT = {(1 / MERE_TO_USD_RATE).toFixed(0)} MERE (1 MERE = {MERE_TO_USD_RATE.toFixed(2)} USDT)
              </div>
            </Card>

            {depositAddress ? (
              <>
                <div className="bg-white p-4 rounded-lg">
                  <div className="w-full aspect-square bg-white rounded-lg flex items-center justify-center">
                    <div className="text-xs font-mono break-all text-center p-4 text-black">
                      {depositAddress}
                    </div>
                  </div>
                </div>

                <div className="space-y-2">
                  <div className="text-sm font-medium">USDT(TRC-20) Address</div>
                  <div className="flex gap-2">
                    <Input
                      value={depositAddress}
                      readOnly
                      className="font-mono text-sm"
                      data-testid="input-deposit-address"
                    />
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={handleCopyAddress}
                      data-testid="button-copy-address"
                    >
                      {copied ? <Check className="w-4 h-4" /> : <Copy className="w-4 h-4" />}
                    </Button>
                  </div>
                </div>

                <Card className="p-4 bg-primary/10">
                  <div className="text-sm">
                    <div className="font-semibold mb-2">Next Steps:</div>
                    <ol className="list-decimal list-inside space-y-1 text-muted-foreground">
                      <li>Copy the address above</li>
                      <li>Send USDT (TRC-20) from your wallet</li>
                      <li>Wait for blockchain confirmations (usually 1-2 minutes)</li>
                      <li>Your MERE balance will be updated automatically</li>
                    </ol>
                  </div>
                </Card>
              </>
            ) : (
              <div className="text-center py-8">
                <Button
                  onClick={() => generateDepositMutation.mutate()}
                  disabled={generateDepositMutation.isPending}
                  data-testid="button-generate-deposit"
                >
                  {generateDepositMutation.isPending ? "Generating..." : "Generate Deposit Address"}
                </Button>
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>

      {/* Withdraw Dialog */}
      <Dialog open={showWithdraw} onOpenChange={setShowWithdraw}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Withdraw USDT (TRC-20)</DialogTitle>
            <DialogDescription>
              Withdraw USDT to your external wallet
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4 py-4">
            <div>
              <label className="text-sm font-medium mb-2 block">Withdrawal Address USDT(TRC-20)</label>
              <Input
                type="text"
                placeholder="USDT(TRC-20) wallet address"
                value={withdrawAddress}
                onChange={(e) => setWithdrawAddress(e.target.value)}
                className="font-mono text-sm"
                data-testid="input-withdraw-address"
              />
            </div>

            <div>
              <label className="text-sm font-medium mb-2 block">Amount (USDT)</label>
              <Input
                type="number"
                placeholder="0.00"
                value={withdrawAmount}
                onChange={(e) => setWithdrawAmount(e.target.value)}
                data-testid="input-withdraw-amount"
              />
              <div className="text-xs text-muted-foreground mt-1">
                Available: {usdtBalance.toFixed(2)} USDT
              </div>
            </div>

            {withdrawAmountUsdt > 0 && (
              <Card className="p-4 space-y-2 bg-accent/20">
                <div className="flex justify-between text-sm">
                  <span className="text-muted-foreground">Amount:</span>
                  <span>{withdrawAmountUsdt.toFixed(2)} USDT</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-muted-foreground">Fee (2%):</span>
                  <span className="text-status-busy">-{withdrawFee.toFixed(2)} USDT</span>
                </div>
                <div className="flex justify-between text-sm pt-2 border-t border-border">
                  <span className="font-semibold">You will receive:</span>
                  <div className="text-right">
                    <div className="font-bold text-primary">{withdrawTotal.toFixed(2)} USDT</div>
                  </div>
                </div>
              </Card>
            )}

            <Card className="p-4 bg-primary/10">
              <div className="text-sm text-muted-foreground">
                <strong>Important:</strong> Only withdraw to a valid USDT(TRC-20) wallet address. Withdrawals are final and cannot be reversed.
              </div>
            </Card>
          </div>

          <DialogFooter>
            <Button variant="outline" onClick={() => setShowWithdraw(false)} data-testid="button-cancel-withdraw">
              Cancel
            </Button>
            <Button
              onClick={handleWithdraw}
              disabled={withdrawAmountUsdt <= 0 || withdrawAmountUsdt > usdtBalance || !withdrawAddress || withdrawMutation.isPending}
              className="bg-gold-gradient text-black font-bold"
              data-testid="button-confirm-withdraw"
            >
              {withdrawMutation.isPending ? "Processing..." : "Confirm Withdrawal"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Convert Dialog */}
      <Dialog open={showConvert} onOpenChange={setShowConvert}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Convert Currency</DialogTitle>
            <DialogDescription>
              Convert between MERE and USDT instantly
            </DialogDescription>
          </DialogHeader>

          <Tabs value={convertDirection} onValueChange={(v) => setConvertDirection(v as any)} className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="mere-to-usdt" data-testid="tab-mere-to-usdt">MERE  USDT</TabsTrigger>
              <TabsTrigger value="usdt-to-mere" data-testid="tab-usdt-to-mere">USDT  MERE</TabsTrigger>
            </TabsList>

            <TabsContent value="mere-to-usdt" className="space-y-4 pt-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Amount (MERE)</label>
                <Input
                  type="number"
                  placeholder="0.00"
                  value={convertAmount}
                  onChange={(e) => setConvertAmount(e.target.value)}
                  data-testid="input-convert-amount-mere"
                />
                <div className="text-xs text-muted-foreground mt-1">
                  Available: {formatMERE(mereBalance)} MERE
                </div>
              </div>

              {parseFloat(convertAmount) > 0 && (
                <Card className="p-4 space-y-2 bg-accent/20">
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Convert:</span>
                    <span>{formatMERE(parseFloat(convertAmount))} MERE</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Exchange Rate:</span>
                    <span>1 MERE = {MERE_TO_USD_RATE.toFixed(2)} USDT</span>
                  </div>
                  <div className="flex justify-between text-sm pt-2 border-t border-border">
                    <span className="font-semibold">You will receive:</span>
                    <div className="text-right">
                      <div className="font-bold text-primary">
                        {mereToUSD(parseFloat(convertAmount)).toFixed(2)} USDT
                      </div>
                    </div>
                  </div>
                </Card>
              )}
            </TabsContent>

            <TabsContent value="usdt-to-mere" className="space-y-4 pt-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Amount (USDT)</label>
                <Input
                  type="number"
                  placeholder="0.00"
                  value={convertAmount}
                  onChange={(e) => setConvertAmount(e.target.value)}
                  data-testid="input-convert-amount-usdt"
                />
                <div className="text-xs text-muted-foreground mt-1">
                  Available: {usdtBalance.toFixed(2)} USDT
                </div>
              </div>

              {parseFloat(convertAmount) > 0 && (
                <Card className="p-4 space-y-2 bg-accent/20">
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Convert:</span>
                    <span>{parseFloat(convertAmount).toFixed(2)} USDT</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Exchange Rate:</span>
                    <span>1 USDT = {(1 / MERE_TO_USD_RATE).toFixed(0)} MERE</span>
                  </div>
                  <div className="flex justify-between text-sm pt-2 border-t border-border">
                    <span className="font-semibold">You will receive:</span>
                    <div className="text-right">
                      <div className="font-bold text-primary">
                        {formatMERE(usdToMERE(parseFloat(convertAmount)))} MERE
                      </div>
                    </div>
                  </div>
                </Card>
              )}
            </TabsContent>
          </Tabs>

          <Card className="p-4 bg-primary/10">
            <div className="text-sm text-muted-foreground">
              <strong>Note:</strong> Conversions are instant with no fees. Exchange rate: 1 MERE = {MERE_TO_USD_RATE.toFixed(2)} USDT
            </div>
          </Card>

          <DialogFooter>
            <Button variant="outline" onClick={() => setShowConvert(false)} data-testid="button-cancel-convert">
              Cancel
            </Button>
            <Button
              onClick={handleConvert}
              disabled={!convertAmount || parseFloat(convertAmount) <= 0 || convertMutation.isPending}
              className="bg-gold-gradient text-black font-bold"
              data-testid="button-confirm-convert"
            >
              {convertMutation.isPending ? "Converting..." : "Confirm Conversion"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <BottomNav />
    </div>
  );
}

function TrendingUp({ className }: { className?: string }) {
  return (
    <svg
      className={className}
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <polyline points="23 6 13.5 15.5 8.5 10.5 1 18" />
      <polyline points="17 6 23 6 23 12" />
    </svg>
  );
}

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* DARK MODE (Default for MereMiners - Premium Black & Gold) */
:root {
  --button-outline: rgba(212, 175, 55, .15);
  --badge-outline: rgba(212, 175, 55, .10);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: 9; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(212, 175, 55, .05);
  --elevate-2: rgba(212, 175, 55, .12);

  --background: 240 5% 4%;

  --foreground: 43 10% 92%;

  --border: 43 10% 12%;

  --card: 240 4% 7%;

  --card-foreground: 43 10% 92%;

  --card-border: 43 20% 10%;

  --sidebar: 240 5% 6%;

  --sidebar-foreground: 43 10% 92%;

  --sidebar-border: 43 10% 12%;

  --sidebar-primary: 43 74% 52%;

  --sidebar-primary-foreground: 43 10% 8%;

  --sidebar-accent: 43 15% 12%;

  --sidebar-accent-foreground: 43 10% 92%;

  --sidebar-ring: 43 74% 52%;

  --popover: 240 4% 8%;

  --popover-foreground: 43 10% 92%;

  --popover-border: 43 15% 15%;

  --primary: 43 74% 52%;

  --primary-foreground: 43 10% 8%;

  --secondary: 240 4% 12%;

  --secondary-foreground: 43 10% 92%;

  --muted: 240 5% 11%;

  --muted-foreground: 43 8% 60%;

  --accent: 43 10% 15%;

  --accent-foreground: 43 10% 88%;

  --destructive: 0 72% 50%;

  --destructive-foreground: 0 0% 100%;

  --input: 43 10% 18%;
  --ring: 43 74% 52%;
  --chart-1: 43 100% 60%;
  --chart-2: 25 95% 58%;
  --chart-3: 151 55% 60%;
  --chart-4: 217 91% 60%;
  --chart-5: 280 67% 60%;

  --font-sans: Poppins, Inter, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'JetBrains Mono', monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.30), 0px 0px 8px 0px hsl(43 74% 52% / 0.05);
  --shadow-xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.35), 0px 0px 10px 0px hsl(43 74% 52% / 0.08);
  --shadow-sm: 0px 2px 0px 0px hsl(0 0% 0% / 0.30), 0px 2px 4px -1px hsl(0 0% 0% / 0.40), 0px 0px 12px 0px hsl(43 74% 52% / 0.10);
  --shadow: 0px 2px 0px 0px hsl(0 0% 0% / 0.30), 0px 2px 4px -1px hsl(0 0% 0% / 0.40), 0px 0px 12px 0px hsl(43 74% 52% / 0.10);
  --shadow-md: 0px 4px 0px 0px hsl(0 0% 0% / 0.35), 0px 4px 6px -1px hsl(0 0% 0% / 0.45), 0px 0px 16px 0px hsl(43 74% 52% / 0.12);
  --shadow-lg: 0px 6px 0px 0px hsl(0 0% 0% / 0.40), 0px 6px 10px -1px hsl(0 0% 0% / 0.50), 0px 0px 20px 0px hsl(43 74% 52% / 0.15);
  --shadow-xl: 0px 10px 0px 0px hsl(0 0% 0% / 0.45), 0px 10px 16px -1px hsl(0 0% 0% / 0.55), 0px 0px 28px 0px hsl(43 74% 52% / 0.18);
  --shadow-2xl: 0px 14px 0px 0px hsl(0 0% 0% / 0.50), 0px 0px 36px 0px hsl(43 74% 52% / 0.20);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
