server/referralService.ts.content:
import { db } from "./db";
import { users, transactions } from "@shared/schema";
import { eq, sql } from "drizzle-orm";
import { REFERRAL_BONUS_PERCENT } from "@shared/constants";

export async function creditReferralBonus(
  refereeUserId: string,
  earningsAmount: number
): Promise<void> {
  if (earningsAmount <= 0) return;

  const referee = await db.query.users.findFirst({
    where: eq(users.id, refereeUserId),
  });

  if (!referee || !referee.referredById) {
    return;
  }

  const referrerId = referee.referredById;

  // Security: Prevent self-referral bonus (should never happen but double-check)
  if (referrerId === refereeUserId) {
    console.error(`[SECURITY] Self-referral detected: User ${refereeUserId} trying to refer themselves`);
    return;
  }

  const bonusAmount = earningsAmount * (REFERRAL_BONUS_PERCENT / 100);

  await db.transaction(async (tx) => {
    await tx
      .update(users)
      .set({
        mereBalance: sql`${users.mereBalance} + ${bonusAmount}`,
        totalReferralEarnings: sql`${users.totalReferralEarnings} + ${bonusAmount}`,
      })
      .where(eq(users.id, referrerId));

    await tx.insert(transactions).values({
      userId: referrerId,
      type: "referral_bonus",
      amountMere: bonusAmount.toFixed(8),
      description: `Referral bonus (${REFERRAL_BONUS_PERCENT}% of friend's earnings)`,
      status: "completed",
      metadata: {
        refereeUserId,
        refereeEarnings: earningsAmount,
      },
    });
  });
}

export async function getReferralStats(userId: string) {
  const user = await db.query.users.findFirst({
    where: eq(users.id, userId),
  });

  if (!user) {
    return null;
  }

  const referrals = await db.query.users.findMany({
    where: eq(users.referredById, userId),
    columns: {
      id: true,
      firstName: true,
      lastName: true,
      email: true,
      totalMined: true,
      createdAt: true,
    },
  });

  const referralTransactions = await db.query.transactions.findMany({
    where: eq(transactions.userId, userId),
    orderBy: (transactions, { desc }) => [desc(transactions.createdAt)],
  });

  const referralBonusTransactions = referralTransactions.filter(
    (t) => t.type === "referral_bonus"
  );

  return {
    totalReferrals: user.totalReferrals || 0,
    totalReferralEarnings: parseFloat(user.totalReferralEarnings || "0"),
    referrals: referrals.map((r) => ({
      id: r.id,
      name: r.firstName && r.lastName ? `${r.firstName} ${r.lastName}` : r.email?.split("@")[0] || "Miner",
      totalMined: parseFloat(r.totalMined || "0"),
      joinedAt: r.createdAt,
    })),
    recentBonuses: referralBonusTransactions.slice(0, 10).map((t) => ({
      id: t.id,
      amount: parseFloat(t.amountMere || "0"),
      description: t.description,
      createdAt: t.createdAt,
    })),
  };
}

client/src/pages/SignIn.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { queryClient, apiRequest } from "@/lib/queryClient";

const signInSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(1, "Password is required"),
});

type SignInForm = z.infer<typeof signInSchema>;

export default function SignIn() {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  const form = useForm<SignInForm>({
    resolver: zodResolver(signInSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const onSubmit = async (data: SignInForm) => {
    setIsLoading(true);
    try {
      await apiRequest("POST", "/api/auth/signin", data);

      toast({
        title: "Welcome back!",
        description: "Successfully signed in",
      });

      // Refresh user data
      await queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });

      // Redirect to mining room
      window.location.href = "/";
    } catch (error: any) {
      toast({
        title: "Sign in failed",
        description: error.message || "Invalid email or password",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-background via-background to-accent/10 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <CardTitle className="text-3xl font-display font-bold">
            <span className="bg-gold-gradient bg-clip-text text-transparent">
              Welcome Back
            </span>
          </CardTitle>
          <CardDescription>
            Sign in to your MereMiners account
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <Button
              type="button"
              variant="outline"
              className="w-full"
              onClick={() => window.location.href = "/api/login"}
              data-testid="button-google-signin"
            >
              <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
                />
                <path
                  fill="currentColor"
                  d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
                />
                <path
                  fill="currentColor"
                  d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
                />
                <path
                  fill="currentColor"
                  d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
                />
              </svg>
              Continue with Google
            </Button>

            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <Separator className="w-full" />
              </div>
              <div className="relative flex justify-center text-xs uppercase">
                <span className="bg-card px-2 text-muted-foreground">
                  Or continue with email
                </span>
              </div>
            </div>

            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input type="email" placeholder="john@example.com" {...field} data-testid="input-email" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <Input type="password" placeholder="" {...field} data-testid="input-password" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full bg-gold-gradient text-black font-bold"
                  disabled={isLoading}
                  data-testid="button-signin"
                >
                  {isLoading ? "Signing In..." : "Sign In"}
                </Button>
              </form>
            </Form>
          </div>
        </CardContent>
        <CardFooter className="flex flex-col gap-4">
          <div className="text-sm text-muted-foreground text-center">
            Don't have an account?{" "}
            <Link href="/signup">
              <span className="text-primary hover:underline cursor-pointer font-semibold">
                Sign Up
              </span>
            </Link>
          </div>
        </CardFooter>
      </Card>
    </div>
  );
}

server/emailAuth.ts.content:
import { db } from "./db";
import { users } from "@shared/schema";
import { eq } from "drizzle-orm";
import bcrypt from "bcrypt";
import { nanoid } from "nanoid";
import type { Request, Response } from "express";

const SALT_ROUNDS = 10;

// Generate unique referral code
function generateReferralCode(): string {
  return `MERE${nanoid(6).toUpperCase()}`;
}

export async function signUp(
  email: string,
  password: string,
  referralCode?: string
) {
  // Validate inputs
  if (!email || !password) {
    throw new Error("Email and password are required");
  }

  if (password.length < 8) {
    throw new Error("Password must be at least 8 characters long");
  }

  // Check if user already exists
  const existing = await db.select().from(users).where(eq(users.email, email)).limit(1);

  if (existing.length > 0) {
    throw new Error("Email already registered");
  }

  // Hash password
  const passwordHash = await bcrypt.hash(password, SALT_ROUNDS);

  // Handle referral
  let referredById: string | null = null;
  if (referralCode) {
    const referrer = await db.select().from(users).where(eq(users.referralCode, referralCode)).limit(1);
    if (referrer.length > 0) {
      referredById = referrer[0].id;
    }
  }

  // Create user
  const newUser = await db.insert(users).values({
    email,
    passwordHash,
    referralCode: generateReferralCode(),
    referredById,
  }).returning();

  // Update referrer's count if applicable
  if (referredById) {
    const [referrer] = await db.select().from(users).where(eq(users.id, referredById)).limit(1);
    if (referrer) {
      await db
        .update(users)
        .set({ totalReferrals: referrer.totalReferrals + 1 })
        .where(eq(users.id, referredById));
    }
  }

  return newUser[0];
}

export async function signIn(email: string, password: string) {
  // Validate inputs
  if (!email || !password) {
    throw new Error("Email and password are required");
  }

  // Find user
  const [user] = await db.select().from(users).where(eq(users.email, email)).limit(1);

  if (!user) {
    throw new Error("Invalid email or password");
  }

  // Check if user has password (Replit Auth users don't)
  if (!user.passwordHash) {
    throw new Error("This account uses Replit Auth. Please log in with Replit.");
  }

  // Verify password
  const isValid = await bcrypt.compare(password, user.passwordHash);

  if (!isValid) {
    throw new Error("Invalid email or password");
  }

  return user;
}

// Helper to get user ID from either session or Replit Auth
export function getUserId(req: Request): string | null {
  if (req.session?.userId) {
    return req.session.userId;
  }
  if (req.user?.claims?.sub) {
    return req.user.claims.sub;
  }
  return null;
}

// Helper that returns userId or sends 401 and returns undefined
export function requireUserId(req: Request, res: Response): string | undefined {
  const userId = getUserId(req);
  if (!userId) {
    res.status(401).json({ message: "Unauthorized" });
    return undefined;
  }
  return userId;
}

